INSERT INTO public.faq_items (category, question, answer, order_index, is_active) VALUES
('general', 'Как оформить заказ?', 'Вы можете оформить заказ через сайт, по телефону или в магазине. При оформлении укажите желаемую дату получения.', 1, true),
('general', 'Какие способы оплаты вы принимаете?', 'Мы принимаем наличные, банковские карты, а также онлайн-платежи через YooKassa.', 2, true),
('general', 'Можно ли внести изменения в заказ?', 'Изменения в заказ можно внести за 24 часа до выдачи. Позвоните нам для уточнения деталей.', 3, true),
('delivery', 'Доставляете ли вы торты?', 'Да, мы доставляем торты по городу. Стоимость доставки зависит от района.', 1, true),
('delivery', 'В какое время работает доставка?', 'Доставка осуществляется с 10:00 до 20:00 ежедневно.', 2, true),
('products', 'Можно ли заказать торт по индивидуальному дизайну?', 'Конечно! У нас есть конструктор тортов, где вы можете создать уникальный дизайн.', 1, true),
('products', 'Сколько хранится торт?', 'Наши торты хранятся 2-3 дня в холодильнике при температуре 2-5°C.', 2, true),
('products', 'Есть ли безглютеновые варианты?', 'Да, мы готовим торты и десерты без глютена. Уточните наличие при заказе.', 3, true);
INSERT INTO public.posts (title, slug, excerpt, content, status, published_at, author_id, category_id, tags, featured) VALUES
('Как выбрать идеальный торт на день рождения', 'kak-vybrat-tort-na-den-rozhdeniya', 'Полный гид по выбору торта, который запомнится надолго', '<h2>Основные критерии выбора торта</h2><p>При выборе торта на день рождения важно учесть несколько ключевых факторов...</p>', 'published', NOW(), '00000000-0000-0000-0000-000000000001', 1, ARRAY['торт', 'день рождения', 'советы'], true),
('Секреты приготовления идеального бисквита', 'sekrety-idealnogo-biskvita', 'Профессиональные секреты от наших кондитеров', '<h2>Ингредиенты для идеального бисквита</h2><p>Качественные ингредиенты — основа успеха...</p>', 'published', NOW() - INTERVAL '2 days', '00000000-0000-0000-0000-000000000002', 1, ARRAY['бисквит', 'рецепт', 'секреты'], false),
('История праздничного торта', 'istoriya-prazdnichnogo-torta', 'Увлекательная история возникновения традиции праздничных тортов', '<h2>Первые праздничные торты</h2><p>Традиция печь торты на праздники насчитывает многие века...</p>', 'published', NOW() - INTERVAL '5 days', '00000000-0000-0000-0000-000000000001', 4, ARRAY['история', 'традиции'], false);
